```java
public class PowerOfThree {
    public static boolean isPowerOfThree(int n) {
        if (n < 1) return false;
        double logValue = Math.log(n) / Math.log(3);
        return Math.floor(logValue) == logValue;
    }

    public static void main(String[] args) {
        System.out.println(isPowerOfThree(27)); // Outputs: true
        System.out.println(isPowerOfThree(0));  // Outputs: false
        System.out.println(isPowerOfThree(-1)); // Outputs: false
