// Import the Discord.JS v14 Client module
const { Client, Intents } = require('discord.js');

// Replace YOUR_BOT_TOKEN with your actual bot token
const TOKEN = 'YOUR_BOT_TOKEN';

// Initialize the client with the required intents
const client = new Client({ 
  intents: [
    Intents.Flags.Guilds,
    Intents.Flags.GuildMessages,
    Intents.Flags.MessageContent,
  ],
});

// Register slash command
client.on('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
  client.application.commands.set([
    {
      name: 'ban',
      description: 'Bans the mentioned user from the server.',
      options: [
        {
          name: 'member',
          description: 'The user to ban.',
          type: 6, // User type
          required: true,
        },
      ],
    },
  ]);
});

client.on('interactionCreate', async (interaction) => {
  if (!interaction.isChatInputCommand()) return;

  const command = interaction.commandName;
  switch (command) {
    case 'ban':
      try {
        const targetMember = interaction.options.getMember('member');
        await interaction.guild.members.ban(targetMember.id, { days: 0 });
        interaction.reply({
          content: `User ${targetMember.user.username}#${targetMember.user.discriminator} has been banned.`,
          ephemeral: true,
        });
      } catch (error) {
        interaction.reply({ content: 'Error occurred while banning user.', ephemeral: true });
        console.error(error);
      }
      break;
    default:
      console.log(`Unknown command: ${command}`);
      break;
  }
});

client.login(TOKEN);
```

