module.exports = ({ Cypress }) => {
  const { execSync } = require('child_process');

  const dockerComposeCommand = 'docker-compose';

  async function runDockerComposeCommand(command, args) {
    return new Promise((resolve, reject) => {
      execSync(`${dockerComposeCommand} ${command} ${args}`, { stdio: ['pipe', process.stdout, process.stderr] }).on('close', resolve);
    });
  }

