#include <iostream>
#include <fstream>
#include <string>

int main() {
    // Specify the path to your text file (replace "input.txt" with your actual filename)
    std::ifstream inputFile("input.txt");

    if (!inputFile.is_open()) {
        std::cerr << "Error opening input file." << std::endl;
        return 1; // Exit with error code
    }

    std::string searchWord = "specific_word"; // Change this to the word you want to count
    int occurrenceCount = 0;

    std::string currentLine;
    while (std::getline(inputFile, currentLine)) { // Read each line of the file
        size_t foundPosition = currentLine.find(searchWord);

        // Check if the word was found at any position
        if (foundPosition != std::string::npos) {
            ++occurrenceCount; // Increment the count
            // If the word is not found at the beginning or end of the string,
            // move it forward until we find another instance of the word.
            while (foundPosition != std::string::npos && (foundPosition > 0 || !currentLine[foundPosition - 1] == ' ') &&
                   (foundPosition + searchWord.length() < currentLine.length() ||
                    !currentLine[currentLine.length() - searchWord.length()] == ' '))
                foundPosition = currentLine.find(searchWord, foundPosition + 1);

            // Move back one character to ensure we don't miss previous instances
            --foundPosition;
        }
    }

    // Close the file after reading
    inputFile.close();

    std::cout << "The word \"" << searchWord << "\" appears " << occurrenceCount << " times in the file.\n";
    return 0;
}
```

