#include <iostream>
#include <fstream>
#include <sstream>
#include <unordered_map>

int main() {
    std::string filename = "words.txt";
    std::string searchWord;
    std::unordered_map<std::string, int> wordCount;

    // Demander au utilisateur de saisir le mot à rechercher
    std::cout << "Veuillez entrer le mot à rechercher: ";
    std::cin >> searchWord;

    // Ouvrir le fichier pour lecture
