pragma solidity ^0.8.0;

import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract TokenMainContract is Ownable {
    ERC20 public token;
    IUniswapV2Router02 public uniswapRouter;
    address public uniswapPair;

    constructor(address _tokenAddress, address _routerAddress) {
        token = ERC20(_tokenAddress);
        uniswapRouter = IUniswapV2Router02(_routerAddress);
    }

    // Add liquidity to Uniswap
    function addLiquidity() external onlyOwner {
        uint256 tokenAmount = token.balanceOf(address(this));
        token.approve(address(uniswapRouter), tokenAmount);

        IUniswapV2Router02.MintParams memory params = IUniswapV2Router02.MintParams(
            address(token),
            address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE), // WETH address
            tokenAmount,
