        require(_participants.length == _amounts.length, "Participants and amounts length mismatch");

        for (uint256 i = 0; i < _participants.length; i++) {
            require(token.transfer(_participants[i], _amounts[i]), "Failed to transfer tokens");
        }
    }
}
