```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

int main() {
    std::string filename;
    std::string wordToCount;
    int count = 0;

    // Prompt user for the filename and the word to count
    std::cout << "Enter the name of the file: ";
    std::cin >> filename;

    std::cout << "Enter the word to count: ";
    std::cin >> wordToCount;

    // Open the file
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return 1;
    }

    std::string line;
    while (std::getline(file, line)) {
        std::istringstream iss(line);
        std::string word;
        while (iss >> word) {
            if (word == wordToCount) {
                count++;
            }
        }
    }

    // Close the file
    file.close();

    // Output the result
    std::cout << "The word \"" << wordToCount << "\" appears " << count << " times in the file." << std::endl;

    return 0;
}
```

