// Basic markdown to HTML converter
std::string markdownToHTML(const std::string& markdown) {
    std::string html = "";
    size_t pos = 0;
    while (pos < markdown.size()) {
        if (markdown[pos] == '#') {
            // Handle headers
            int level = 1;
            while (pos + level < markdown.size() && markdown[pos + level] == '#') {
                ++level;
            }
            html += "<h" + std::to_string(level) + ">";
            pos += level;
            while (pos < markdown.size() && markdown[pos] != '\n') {
                html += markdown[pos++];
            }
            html += "</h" + std::to_string(level) + ">\n";
            continue;
        } else if (markdown[pos] == '*' && pos + 1 < markdown.size() && markdown[pos + 1] == '*') {
            // Handle bold text
            html += "<strong>";
            pos += 2;
            while (pos < markdown.size() && markdown[pos] != '*' || (pos > 0 && markdown[pos - 1] != '*')) {
                html += markdown[pos++];
            }
            html += "</strong>";
            continue;
        } else if (markdown[pos] == '*' && pos + 1 < markdown.size() && markdown[pos + 1] == '*') {
            // Handle italic text
            html += "<em>";
            pos += 2;
            while (pos < markdown.size() && markdown[pos] != '*' || (pos > 0 && markdown[pos - 1] != '*')) {
                html += markdown[pos++];
            }
            html += "</em>";
            continue;
        } else if (markdown[pos] == '`' && pos + 1 < markdown.size() && markdown[pos + 1] == '`') {
            // Handle inline code
            html += "<code>";
            pos += 2;
            while (pos < markdown.size() && markdown[pos] != '`' || (pos > 0 && markdown[pos - 1] != '`')) {
                html += markdown[pos++];
            }
            html += "</code>";
            continue;
        }
        // Copy other characters directly to HTML
        html += markdown[pos++];
    }
    return html;
}

int main() {
    std::string markdown = "# Header\n\n*Italic*\n\n**Bold**\n\n`Inline Code`";
    std::string expected_html = "<h1>Header</h1>\n\n<p><em>Italic</em></p>\n\n<p><strong>Bold</strong></p>\n\n<p><code>Inline Code</code></p>";

    std::string html = markdownToHTML(markdown);
    assert(html == expected_html);

    std::cout << "Markdown converted successfully!" << std::endl;
    std::cout << html << std::endl;

    return 0;
}
