    std::string filename = "input.txt";  // The name of the input file
    std::string searchWord = "example";  // The word to search for

    // Open the file
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error: Could not open file " << filename << std::endl;
        return 1;
    }

    int count = 0;  // Counter for occurrences of the word

    std::string line;
    while (std::getline(file, line)) {
        std::istringstream iss(line);  // Use a string stream to parse each line
        std::string word;

        while (iss >> word) {  // Extract words from the line
            if (word == searchWord) {
                count++;  // Increment the count if the word matches
            }
        }
    }

    // Close the file
    file.close();

    // Output the result
    std::cout << "The word '" << searchWord << "' appears " << count << " times in the file." << std::endl;

    return 0;
}
