\newcommand{\blackking}{\node at (0,0) {\textcolor{white}{k}};}

\begin{document}

% Function to draw the chessboard with pieces
\newcommand{\drawchessboard}[1]{
    \begin{tikzpicture}
        % Draw the chessboard grid
        \foreach \x in {0,...,\chessboardsize} {
            \foreach \y in {0,...,\chessboardsize} {
                \fill[black!50] (\x*\squaresize, \y*\squaresize) rectangle ++(\squaresize, \squaresize);
            }
        }

        % Parse the FEN string and place the pieces
        \def\FENstring{#1}
        \edef\FENparsed{\noexpand\parseFEN{\FENstring}}
        \FENparsed

        % Define the parsing function
        \def\parseFEN#1{
            \foreach \row in #1 {
                \pgfmathtruncatemacro\rowindex{mod(\row,10)}
                \edef\currentrow{\noexpand\processrow{\rowindex}}
                \currentrow
            }
        }

        % Define the row processing function
        \def\processrow#1{
            \pgfmathtruncatemacro\rowindex{#1}
            \edef\currentrow{\noexpand\drawpieces{\rowindex}}
            \currentrow
        }

        % Define the piece placement function
        \def\drawpieces#1{
            \edef\currentrow{\noexpand\splitrow{#1}}
            \currentrow
        }

        % Split the row into individual elements
        \def\splitrow#1{
            \edef\currentelement{}
            \foreach \chara in {#1} {
                \edef\currentelement{\currentelement\chara}
                \ifnum\currentelement=1
                    \drawpiece{whitepawn}
                \fi
                \ifnum\currentelement=2
                    \drawpiece{blackpawn}
                \fi
                \ifnum\currentelement=3
                    \drawpiece{whiterook}
                \fi
                \ifnum\currentelement=4
                    \drawpiece{blackrook}
                \fi
                \ifnum\currentelement=5
                    \drawpiece{whiteknight}
                \fi
                \ifnum\currentelement=6
                    \drawpiece{blackknight}
                \fi
                \ifnum\currentelement=7
                    \drawpiece{whitebishop}
                \fi
                \ifnum\currentelement=8
                    \drawpiece{blackbishop}
                \fi
                \ifnum\currentelement=9
                    \drawpiece{whitequeen}
                \fi
                \ifnum\currentelement=10
                    \drawpiece{blackqueen}
                \fi
                \ifnum\currentelement=11
                    \drawpiece{whiteking}
                \fi
                \ifnum\currentelement=12
                    \drawpiece{blackking}
                \fi
                \edef\currentelement{}
            }
        }

        % Draw the piece at the specified coordinates
        \def\drawpiece#1{
            \node[anchor=south west,inner sep=0] at (#1*\squaresize, #2*\squaresize) {\csname #1\endcsname};
        }
    \end{tikzpicture}
}

