    std::unordered_map<std::string, std::string> markdownToHtmlMap = {
        {"#", "<h1>"},
        {"##", "<h2>"},
        {"###", "<h3>"},
        {"----", "<hr/>"}, 
        {"**", "<b>"},
        {"***", "<i>"},
        {"~~", "<u>"},
        {"_", "<sub>"},
        {"*", "<em>"},
        {"_", "<i>"},
        {"/", "<i>"},
        {"[link text](link url)", "<a href=\"{link url}\"> {link text}</a>"},
        {">", "</b>"},
        {"?", "<b>"},
        {"[image text](https://example.com/image.png)", "<img src=\"{image.png}\"" />}
    };

    std::replace(markdown.begin(), markdown.end(), '\n', '<br>'); 
    std::string html = "";

    for (char &c : markdown) { 
        if (c == '<')
            html += ">";

        if(std::isdigit(c))
            html += c;

        if (markdownToHtmlMap.find(std::string(1, c)) != markdownToHtmlMap.end()) 
            html += markdownToHtmlMap[std::string(1, c)]; 


    }
    return html;
}

