    public static boolean isPowerOfThree(int n) {
        if (n <= 0) {
            return false; 
        }

        while (n % 3 == 0) {
            n /= 3; 
        }
        return n == 1; // Base case: if the number is 1, it's a power of 3
    }

    public static void main(String[] args) {
        System.out.println(isPowerOfThree(27) ? "27 is a power of 3" : "27 is not a power of 3"); 
