using namespace std;

int main() {
  // Define the word to count
  string wordToCount = "hello";

  // Input file name
  string filename = "input.txt";

  ifstream inputFile(filename);

  // Check if the file is opened successfully
  if (!inputFile.is_open()) {
    cerr << "Error opening file: " << filename << endl;
    return 1;
  }

  // Counter for the word's count
  int count = 0;

  // Iterate through each line of the file
  string line;
  while (getline(inputFile, line)) {
    // Convert the line to a stringstream for easier word counting
    stringstream ss(line);
    string currentWord;

    while (getline(ss, currentWord, ' ')) {
      // Compare the current word with the target word
      if (currentWord == wordToCount) {
        count++;
      }
    }
  }

  // Print the result
  cout << "The word '" << wordToCount << "' occurs " << count << " times in the file." << endl;

  // Close the file
  inputFile.close();

  return 0;
}
```


