    stringstream ss(line);
    string word;
    while (ss >> word) {  // Extract words into the word variable
      if (word == wordToFind) {
        count++;
      }
    }
  }

  // Close the file
  file.close();

  // Display the count
  cout << "Number of occurrences of '" << wordToFind << "': " << count << endl;

  return 0;
}
