        printf("Error creating texture\n");
        return NULL;
    }

    return snakeTexture;
}

// Function to draw the game
void drawGame(SDL_Texture* screen, Score* score) {
    // Clear screen
    SDL_SetRenderDrawColor(screen, 0x00, 0, 0, 0);
    SDL_RenderClear(screen);

    // Draw snake
    if (score->score > 0) {
        SDL_Rect snakeRect = { score->x, score->y, 10, 10 };
        SDL_RenderFillRect(screen, &snakeRect);
    }

    // Draw food
