    } else if (e.key.keysym.sym == SDLK_LEFT) {
        if (score->direction == 0) {
            score->direction = 3;
        } else if (score->direction == 1) {
            score->direction = 0;
        } else if (score->direction == 2) {
            score->direction = 1;
        }
    }
}

// Game loop
int main(int argc, char* argv[]) {
    // Initialize SDL
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("Error initializing SDL\n");
        return -1;
    }

    // Create game screen
    SDL_Window* screen = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, WIN_WIDTH, WIN_HEIGHT, SDL_WINDOW_SHOWN);
    if (screen == NULL) {
        printf("Error creating window\n");
        return -1;
    }

    // Load images
    Score* snake = createSnakeTexture();
    if (snake == NULL) {
        printf("Error loading snake image\n");
        SDL_Quit();
        return -1;
