    // Create game renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(screen, -1, SDL_RENDERER_ACCELERATED);
    if (renderer == NULL) {
        printf("Error creating renderer\n");
        SDL_DestroyTexture(snake);
        SDL_FreeSurface(snakeSurface);
        SDL_DestroyWindow(screen);
        SDL_Quit();
        return -1;
    }

    // Main game loop
    bool running = true;
    SDL_Event e;
    while (running) {
        while (SDL_PollEvent(&e)) {
            handleKeyboard(e);
        }

        // Update game state
        if (e.type == SDL_QUIT) {
            running = false;
        }

        // Update score
        updateScore(snake->score, snake->lives);

        // Animate game
