        SDL_DestroyTexture(snakeTexture);
    }
    if (foodTexture!= NULL) {
        SDL_FreeSurface(foodSurface);
    }
    if (screen!= NULL) {
        SDL_DestroyRenderer(renderer);
    }
    SDL_DestroyWindow(screen);
    SDL_Quit();
    return 0;
}

int main(int argc, char* argv[]) {
    main();
