/**
 * Checks if all numbers in the array are valid prime numbers
 * 
 * @param {number[]} numbers - An array of numbers
 * @returns {boolean} - True if all numbers in the array are prime, false otherwise
 */
function isValidPrime(numbers) {
    // Function to check if a number is prime
    function isPrime(num) {
        if (num <= 1) return false;
        for (let i = 2; i <= Math.sqrt(num); i++) {
            if (num % i === 0) return false;
        }
        return true;
    }

    // Check if all numbers in the array are prime
    for (let num of numbers) {
        if (!isPrime(num)) return false;
    }
    return true;
}

// Example usage:
let numbers = [2, 3, 5, 23, 30];
console.log(isValidPrime(numbers));  // Output: true

let invalidNumbers = [1, 4, 10, 15];
console.log(isValidPrime(invalidNumbers));  // Output: false
```
