#include <time.h>

#define MAXSIZE 1000000

// Structure to hold the run-length count
typedef struct {
    int count;
    int cnt;
} run;

// Function to count the run-length
int countRuns(int arr[], int n) {
    run count[1000000];
    int i, j, min, k;

    // Create an array to store the run counts
    for (i = 0; i < n; i++) {
        if (arr[i] == arr[i + 1]) {
            count[i].cnt++;
        } else {
            min = count[i].cnt;
            count[i].count = 1;
            count[i].cnt = 0;

            // Find the length of the current run
            for (j = i; j <= i + 3 * min; j++) {
                if (arr[j] == arr[j - 1]) {
                    count[i].cnt += count[j].cnt;
                    count[i].count += 1;
                }
            }
            min = count[i].cnt;
            count[i].count = min;
        }
    }

    return count[0].count;
}

// Function to sort subarrays using Timsort
void timsort(int arr[], int n) {
    if (n <= 1)
        return;

    int m = n / 2;
    int left[n], right[n];

    // Split the array into two halves
    int i = 0;
    int j = 0;
    while (j < m) {
        if (i < m) {
            if (arr[i] < arr[i + 1]) {
                swap(arr[i], arr[j]);
                i++;
                j++;
            } else {
                i++;
            }
        } else {
            i = 0;
        }
    }

    // Recursively sort the left and right halves
    timsort(arr, i);
    timsort(arr + i, m);

    // Merge the sorted halves
    int k = 0;
    int i1 = i, j = 0, k1 = 0;
    while (j < m) {
        if (i1 == m) {
            right[k++] = arr[j];
            j++;
        } else if (j == m) {
            left[k++] = arr[i1];
            i1++;
        } else if (arr[i1] < arr[i1 + 1]) {
            right[k++] = arr[i1];
            i1++;
        } else {
            left[k++] = arr[j];
            j++;
        }
    }
    while (k < m) {
        right[k++] = arr[i1];
        i1++;
    }

    // Copy the merged subarray back to the original array
    for (k = i; k < m; k++) {
        arr[k] = right[k];
    }
}

// Function to swap two elements
void swap(int arr[], int i, int j) {
    int temp = arr[i];
    arr[i] = arr[j];
