// Function to convert markdown headings to HTML
std::string markdown_to_html(const std::string& markdown_string) {
    std::istringstream iss(markdown_string);
    std::vector<std::string> tokens;
    std::string token;

    // Split the input string into tokens (lines and text)
    while (iss >> token) {
        tokens.push_back(token);
    }

    std::vector<std::string> headings;
    std::vector<std::string> line;

    // Iterate through each token, checking if it's a heading (i.e., starts with '#', '-', '**', or '---')
    for (const auto& t : tokens) {
        // Remove leading and trailing whitespace
        while (!t.empty() && std::isspace(t[0])) {
            t.erase(0, 1);
        }
        while (!t.empty() && std::isspace(t.back())) {
            t.pop_back();
        }

        // Check if the token is a heading
        if (t == "#" || t == "**" || t == "---") {
            // If it's a heading, split it into lines and strip leading/trailing whitespace
            line.clear();
            while (iss >> line) {
                line.erase(0, 1);
                while (!line.empty() && std::isspace(line.back())) {
                    line.pop_back();
                }
                if (!line.empty()) {
                    headings.push_back(line);
                }
            }
            if (!headings.empty()) {
                // If there are multiple headings, wrap the entire text in <h1> tags
                std::string html_string = "<h1>" + std::join(headings, "\n</h1>";
                return html_string;
            }
            // If there's only one heading, just add it as it is
            else {
                std::string html_string = "<h1>" + t + "</h1>";
                return html_string;
            }
        }
        // If it's not a heading, add it to the current line
        else {
            if (line.empty()) {
                line.push_back(t);
            } else {
                line.push_back(t);
            }
        }
    }

    // If there are any remaining lines, wrap them in <p> tags
    while (!line.empty()) {
        std::string html_string = "<p>" + line + "</p>";
        line.clear();
        if (!line.empty()) {
            line.push_back(html_string);
        }
    }

    return std::string(line.empty()? "" : "</p><p>\n");
}
