```cpp
#include <iostream>
#include <string>
#include <regex>

std::string markdownToHtml(const std::string& markdownText) {
    std::regex pattern("\\[(.*?)\\]\\((.*?)\\s*(\"(.*?)\")?\\s*(\"(.*?)\")?\\s*(\\((.*?)\\)");
    std::regexEscapeEscape(pattern);
    auto matchBegin = std::sregex_iterator(markdownText.begin(), markdownText.end(), pattern);
    auto matchEnd = std::sregex_iterator();

    std::string htmlOutput;

    for (std::sregex_iterator i = matchBegin; i!= matchEnd; ++i) {
        std::smatch match = *i;

        std::string text = match.str(1);
        std::string title = match[2].str();
        std::string url = match[3].str();
        std::string link = match[4] == ""? match[4] : match[4].str();

        std::string escapedTitle = std::regexEscapeEscape(title);
        htmlOutput += "<a href=\"" + url + "\">" + escapedTitle + "</a>";

        if (!link.empty()) {
            htmlOutput += "<a href=\"" + link + "\">" + link + "</a>";
        }

        if (match[5]!= match[5].str()) {
            htmlOutput += "<br>";
        }

        htmlOutput += text;
    }

    return htmlOutput;
}

// Helper function to escape special characters in a string
std::string std::regexEscapeEscape(const std::string& str) {
    std::string escaped = str;
    for (size_t i = 0; i < str.length(); ++i) {
        if (str[i] == '\\') {
            escaped += "\\" + std::string(1, i + 1);
            i++;
        } else {
            escaped += str[i];
        }
    }
    return escaped;
}

int main() {
    std::string markdownText = "# Hello, World!\n\nThis is a test.\n[Link 1](http://example.com)\n[Link 2](http://example.org)\n\n[Title](http://example.net)";
    std::cout << markdownToHtml(markdownText) << std::endl;

    return 0;
}
```

