    translateWord(word) {
        // Use a simple regex to find the word in the new word table
        let match = word.match(/[IL9op(.*?)]/g);
        if (match) {
            // Get the matched word
            let matchedWord = match[1];

            // Check if the matched word is in the translations table
            if (matchedWord in this translations) {
                // Return the translated word
                return this.translations[matchedWord];
            } else {
                // Return the original word if not found
                return word;
            }
        } else {
            // Return the original word if no match found
