        point.y += snake_image[i].dy;
        snake_image[i].x = point.x;
        snake_image[i].y = point.y;
    }

    // Draw the snake
    for (int i = 0; i < snake_image[0].x * 3; i++) {
        for (int j = 0; j < snake_image[0].y * 3; j++) {
            if (i < 16) {
                printf("R\n");
            } else {
