    }

    // Change the direction of the snake
    switch (snake.direction) {
        case UP:
            for (int i = 0; i < 4; i++) {
                Point point = snake_image[i];
                point.y -= snake_image[i].dy;
                snake_image[i].x = point.x;
                snake_image[i].y = point.y;
            }
            break;
        case DOWN:
            for (int i = 0; i < 4; i++) {
                Point point = snake_image[i];
                point.y += snake_image[i].dy;
                snake_image[i].x = point.x;
                snake_image[i].y = point.y;
            }
            break;
        case LEFT:
            for (int i = 0; i < 4; i++) {
                Point point = snake_image[i];
                point.x -= snake_image[i].dx;
                snake_image[i].x = point.x;
                snake_image[i].y = point.y;
            }
            break;
        case RIGHT:
            for (int i = 0; i < 4; i++) {
                Point point = snake_image[i];
                point.x += snake_image[i].dx;
                snake_image[i].x = point.x;
                snake_image[i].y = point.y;
            }
    }
}

// Function to move the snake forward
void move_snake() {
    Point new_point = {0, 0, 0, RIGHT};
    switch (snake.direction) {
        case UP:
            new_point.y -= 1;
            break;
        case DOWN:
            new_point.y += 1;
            break;
        case LEFT:
            new_point.x -= 1;
            break;
        case RIGHT:
            new_point.x += 1;
            break;
