    snake.y = new_point.y;
    snake.dx = snake_image[new_point.x].dx;
    snake.dy = snake_image[new_point.y].dy;

    // Reset the image for the next frame
    for (int i = 0; i < 4; i++) {
        snake_image[i].x = snake.x;
        snake_image[i].y = snake.y;
    }
}

// Function to change the direction of the snake
