                break;
            case's':
                change_direction(DOWN);
                break;
            case 'a':
                change_direction_LEFT;
                break;
            case 'd':
                change_direction(RIGHT);
                break;
        }

        // Update the game
        move_snake();
        draw_snake(snake_start);

        // Update the frame counter
        frame_counter++;

        // Limit the frame rate
        if (frame_counter >= fps) {
            // Increase the frame counter
            frame_counter = 0;

            // Increase the time
            clock_t now = time(NULL);
            sleep(1 / 60.0);
        }

        // Print the time
        printf("FPS: %f\n", 1.0 / (clock() - start_time));

        // Sleep for 10 milliseconds
        usleep(10 * 1000);
    }

    return 0;
}
