```javascript
const humanInput = document.getElementById('human-input');
const botInput = document.getElementById('bot-input');
const sendBtn = document.getElementById('send-btn');
const chatHistoryDiv = document.getElementById('chat-history');
const chatBotOutputsDiv = document.getElementById('chat-bot-outputs');
const humanOutputDiv = document.getElementById('human-output');

sendBtn.addEventListener('click', handleSendMessage);

humanInput.addEventListener('input', handleHumanInput);
botInput.addEventListener('input', handleBotInput);

function handleSendMessage() {
    const message = humanInput.value;
    humanOutputDiv.innerHTML = '';

    if (message) {
        chatHistoryDiv.appendChild(createHistoryRow(message));
        chatBotOutputsDiv.appendChild(createOutputRow(message));
    }
}

function createHistoryRow(message) {
    const row = document.createElement('tr');
    row.innerHTML = `
        <td>${humanInput.value}</td>
        <td class="bot"></td>
        <td class="bot-output"></td>
    `;
    chatHistoryDiv.appendChild(row);
    return row;
}

function createOutputRow(message) {
    const row = document.createElement('tr');
    row.innerHTML = `
        <td class="bot"></td>
        <td>${message}</td>
    `;
    chatBotOutputsDiv.appendChild(row);
    return row;
}

function handleBotInput() {
    const botMessage = botInput.value.trim(); // Remove leading and trailing whitespace

    // Clean the input before sending it to the bot
    botMessage = botMessage.replace(/[^\w\s]/g, '').trim();

    const translatedMessage = humanInput.value === ''? botMessage : humanInput.value;

    // Update the chat history and outputs
    chatHistoryDiv.innerHTML = '';
    humanOutputDiv.innerHTML = '';
    chatBotOutputsDiv.innerHTML = '';
    chatHistoryDiv.appendChild(createHistoryRow(translatedMessage));
    chatBotOutputsDiv.appendChild(createOutputRow(translatedMessage));

    // Send the translated message to the bot
    sendBotMessage(botMessage);
}

function sendBotMessage(message) {
    fetch('/send', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ message: message })
    })
   .then((response) => {
        if (response.ok) {
            console.log('Message sent to bot successfully!');
        } else {
            console.error('Error sending message to bot:', response.status);
        }
    })
   .catch((error) => {
        console.error('Error sending message to bot:', error);
    });
}

