function translateJSONToString(data, language) {
  const translatedData = {};
  for (const key in data) {
    if (data.hasOwnProperty(key)) {
      const value = data[key];
      if (typeof value === 'object' && value && key in translations[language]) {
        translatedData[key] = translateJSONToString(value, language);
      } else {
        translatedData[key] = value;
      }
    }
  }
  return JSON.stringify(translatedData, null, 2);
}

// Translate the input data
