#include <pugixml.hpp>
#include <string>

/**
 * @brief Converts markdown text to HTML text.
 * 
 * @param markdown A string containing markdown formatted text.
 * @return A string containing the equivalent HTML formatted text.
 */
std::string markdownToHtml(const std::string& markdown) {
    pugi::xml_document doc;
    pugi::xml_parse_result result = doc.load_string(markdown.c_str());
    if (!result) {
        // Handle parse error
        return "";
    }

    // Create an element to hold the HTML content
    pugi::xml_node root = doc.child("html");
    if (!root) {
        // Handle root node error
        return "";
    }

    // Add head and body elements to the document
    root.append_child("head").append_attribute("lang") = "en";
    root.append_child("body");

    // Copy the markdown content into the body
    pugi::xml_node body = root.child("body");
    pugi::xml_parse_result result2 = body.append_copy(doc.document_element());
    if (!result2) {
        // Handle copy error
        return "";
    }

    // Convert the xml to a string
    std::stringstream ss;
    doc.save(ss);
    std::string html = ss.str();

    return html;
}
```

