### Code:

```c
#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <string.h>

#define SCREEN_WIDTH 640
#define SCREEN_HEIGHT 480
#define MAX_FPS 60

// Define directions
typedef enum {
    UP,
    DOWN,
    LEFT,
    RIGHT
} Direction;

// Snake game structure
typedef struct {
    int x, y;
    int width, height;
    Direction direction;
} Snake;

// Food structure
typedef struct {
    int x, y;
} Food;

// Initialize the SDL window
SDL_Window* initSDL() {
    SDL_Init(SDL_INIT_VIDEO);
    SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, 0);
    SDL_SetHint(SDL_HINT_RENDER_SCALE_QUALITY, "1");
    return SDL_CreateWindowSurface(NULL);
}

// Draw the snake and food
void draw(SDL_Surface* surface, SDL_Renderer* renderer, Snake snake, Food food) {
    SDL_Rect snake_rect = {snake.x, snake.y, snake.width, snake.height};
    SDL_Rect food_rect = {food.x, food.y, 10, 10};
    SDL_Rect head_rect = {snake.x, snake.y, snake.width, snake.height};

    // Clear the screen
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
    SDL_RenderClear(renderer);

    // Draw the snake
    SDL_SetRenderDrawColor(renderer, 255, 0, 0, 255);
    SDL_RenderFillRect(renderer, &head_rect);

    for (int i = 1; i < 20; i++) {
        head_rect.x = snake.x + (i * 10);
        SDL_RenderFillRect(renderer, &head_rect);
    }

    // Draw the food
    SDL_SetRenderDrawColor(renderer, 0, 0, 255, 255);
    SDL_RenderFillRect(renderer, &food_rect);

    // Update the screen
    SDL_RenderPresent(renderer);
}

// Main function
int main(int argc, char* argv[]) {
    // Initialize SDL
    SDL_Surface* screen = initSDL();
    SDL_Renderer* renderer = SDL_GetRenderer(screen);

    // Load images
    SDL_Surface* background = IMG_Load("background.png");
    SDL_Texture* background_texture = SDL_CreateTextureFromSurface(renderer, background);
    SDL_FreeSurface(background);

    // Initialize the snake and food
    Snake snake = {10, 10, 10, 10, UP};
    Food food = {100, 100};

    // Game loop
    int running = 1;
    SDL_Event event;
    int fps = 0;
    int ticks = SDL_GetTicks();

    while (running) {
        // Handle events
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                running = 0;
            }
            if (event.type == SDL_KEYDOWN) {
                if (event.key.keysym.sym == SDLK_UP && snake.direction!= DOWN) {
                    snake.direction = UP;
                } else if (event.key.keysym.sym == SDLK_DOWN && snake.direction!= UP) {
                    snake.direction = DOWN;
                } else if (event.key.keysym.sym == SDLK_LEFT && snake.direction!= RIGHT) {
                    snake.direction = LEFT;
                } else if (event.key.keysym.sym == SDLK_RIGHT && snake.direction!= LEFT) {
                    snake.direction = RIGHT;
                }
            }
        }

        // Move the snake
        switch (snake.direction) {
            case UP:
                snake.y -= 10;
                break;
            case DOWN:
                snake.y += 10;
                break;
            case LEFT:
                snake.x -= 10;
                break;
            case RIGHT:
                snake.x += 10;
                break;
        }

        // Draw the background
        SDL_Rect background_rect = {0, 0, SCREEN_WIDTH, SCREEN_HEIGHT};
        SDL_RenderCopy(renderer, background_texture, NULL, &background_rect);

        // Check for collision with food
        if (snake.x >= food.x && snake.x <= food.x + 10 && snake.y >= food.y && snake.y <= food.y + 10) {
            food.x = rand() % (SCREEN_WIDTH - 10) + 1;
            food.y = rand() % (SCREEN_HEIGHT - 10) + 1;
        }

        // Draw the snake and food
        draw(screen, renderer, snake, food);

        // Cap the frame rate
        SDL_Delay(1000 / MAX_FPS);

        // Update the game logic
        int now = SDL_GetTicks();
        if (now - ticks >= 1000) {
            ticks = now;
            fps++;
        }
    }

    // Clean up
    SDL_DestroyTexture(background_texture);
    SDL_FreeSurface(background);
    SDL_DestroyRenderer(renderer);
    SDL_Quit();
    return 0;
}
```

### Note:

* You will need to replace `background.png` with your own image file.
* The snake's movement speed is currently 10 pixels per frame. You can adjust this by changing the movement in the `main` function.
* The game will continue to run until the window is closed.

### Compiling:

To compile the code, use the following command:

```bash
