#include <string>
#include "markdown/markdown.h"
#include <stdexcept>  // for std::runtime_error

/**
 * Converts markdown formatted text to html formatted text.
 *
 * @param markdownText The markdown text to convert.
 * @return The equivalent html text, or an empty string if conversion failed.
 */
std::string markdown_to_html(const std::string& markdownText) {
    std::string htmlText;
    markdown::Converter converter;

    try {
        // Attempt to convert markdown text to html
        htmlText = converter.convert(markdownText);
    } catch (const markdown::Exception& e) {
        // Handle markdown conversion errors
        throw std::runtime_error("Markdown conversion failed: " + std::string(e.what()));
    } catch (const std::exception& e) {
        // Handle any other exceptions
        throw std::runtime_error("An error occurred: " + std::string(e.what()));
    }

    return htmlText;
}

