```cpp
#include <iostream>
#include <fstream>
#include <sstream>
#include <string>

int main() {
    // Specify the file to read from
    std::string filename = "filename.txt";

    // Check if the file exists
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Unable to open file";
        return 1;
    }

    // Count the word occurrences
    int count = 0;
    std::string line, word_to_count;

    // Prompt user for word to count
    std::cout << "Enter the word you wish to count: ";
    std::cin >> word_to_count;

    // Strip punctuation from the word to count
    while (!word_to_count.empty() &&!isalnum(word_to_count.back())) {
        word_to_count.pop_back();
    }

    // Skip this prompt if word has been counted previously in this run of the program
    if (!std::cin || std::cin.fail()) {
        std::cerr << "Invalid input";
        return 1;
    }

    while (std::getline(file, line)) {
        std::istringstream iss(line);
        std::string word;
        while (iss >> word) {
            // Strip punctuation from the word
            while (!word.empty() &&!isalnum(word.back())) {
                word.pop_back();
            }

            // Convert to lowercase for case-insensitive comparison
            for (char& c : word) {
                c = tolower(c);
            }

            if (word == word_to_count) {
                count++;
            }
        }
    }

    file.close();

    // Display the count
    std::cout << "The word \"" << word_to_count << "\" appeared " << count << " times." << std::endl;

    return 0;
}
```

