    name: 'ban',
    description: 'Ban a user from the server.',
    defaultPermission: false,
    options: [
        {
            name: 'user',
            description: 'The user to ban.',
            type: 'User',
            required: true,
        },
    ],

    async execute(interaction) {
        const member = interaction.options.getUser('user');
        if (member.id === interaction.client.user.id) {
            return interaction.reply('You cannot ban me!');
        }
        const memberRole = interaction.guild.roles.cache.find(role => role.id === '@everyone');
        if (memberRole.comparePositions(member.roles.highest) > 0) {
            return interaction.reply('You cannot ban users above you in the role hierarchy!');
