#include <regex>

std::string markdownToHtml(const std::string& markdownText) {
    std::string html;

    // Parse headings
    std::regex headingRegex("^#+ (.*)$", std::regex_constants::multiline);
    std::smatch headingMatch;
    if (std::regex_search(markdownText, headingMatch, headingRegex)) {
        std::string heading = headingMatch[1].str;
        for (size_t i = 0; i < headingMatch[0].length() - 2; ++i) {
            html += "<h" + std::to_string(headingMatch[0].length() - i) + ">" + heading + "</h" + std::to_string(headingMatch[0].length() - i) + ">";
            markdownText = std::regex_replace(markdownText, std::regex("^#+.*$"), "");
        }
    }

    // Parse bold text
    std::regex boldRegex("\\*([^\\*]+\\*)");
    std::smatch boldMatch;
    while (std::regex_search(markdownText, boldMatch, boldRegex)) {
        html += "<b>" + boldMatch[1].str + "</b>";
        markdownText = std::regex_replace(markdownText, boldRegex, "");
    }

    // Parse italics text
    std::regex italicsRegex("_([^_]+_)");
    std::smatch italicsMatch;
    while (std::regex_search(markdownText, italicsMatch, italicsRegex)) {
        html += "<i>" + italicsMatch[1].str + "</i>";
        markdownText = std::regex_replace(markdownText, italicsRegex, "");
    }

    // Any remaining text is plain text
    html += markdownText;

    return html;
}
