/**
 * Function to count the occurrences of a word in a file.
 *
 * @param filename The name of the file to read from.
 * @param targetWord The word to search for.
 * @return The frequency of the target word in the file.
 */
int countWord(const std::string& filename, const std::string& targetWord) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error: Unable to open file " << filename << std::endl;
        return -1;
    }

    std::unordered_map<std::string, int> wordCounts;
    std::string word;

    while (file >> word) {
        // Convert to lowercase to ensure case-insensitive comparison
        for (char& c : word) {
            c = std::tolower(c);
        }

        // Trim leading and trailing whitespace
        while (!word.empty() && std::isspace(word[0])) {
            word.erase(0, 1);
        }
        while (!word.empty() && std::isspace(word[word.size() - 1])) {
            word.pop_back();
        }

        if (!word.empty()) {
            // Update word count
            ++wordCounts[word];

            // Check if this is the target word
            if (word == targetWord) {
                return wordCounts[word];
            }
        }
    }

    // Return the count for the target word, or 0 if not found
    return wordCounts.count(targetWord)? wordCounts[targetWord] : 0;
}

int main() {
    std::string filename;
    std::cout << "Enter the filename: ";
    std::cin >> filename;

    std::string targetWord;
    std::cout << "Enter the word to search for: ";
    std::cin >> targetWord;

    int count = countWord(filename, targetWord);
    if (count!= -1) {
        std::cout << "The word \"" << targetWord << "\" appears " << count << " times in " << filename << std::endl;
    } else {
        std::cout << "Error: Unable to count occurrences of \"" << targetWord << "\"" << std::endl;
