    <div id="chat-window">
        <div class="message" style="background-color: #F2F2F2; padding: 10px; border-bottom: 1px solid #ccc;">
            <span style="font-weight: bold;">User</span> : Hello! How are you?
        </div>
        <div class="message" style="background-color: #F2F2F2; padding: 10px; border-bottom: 1px solid #ccc;">
            <span style="font-weight: bold;">Chatbot</span> : I'm doing great, thanks! What can I help you with today?
        </div>
        <div class="message" style="background-color: #F2F2F2; padding: 10px; border-bottom: 1px solid #ccc;">
            <span style="font-weight: bold;">User</span> : I'm feeling a bit stuck with my project. Can you help me?
        </div>
        <div class="message" style="background-color: #F2F2F2; padding: 10px; border-bottom: 1px solid #ccc;">
            <span style="font-weight: bold;">Chatbot</span> : Of course! What specifically are you struggling with?
        </div>
    </div>
    <input type="text" id="user-input" placeholder="Type your message here...">
    <button id="send-btn">Send</button>
    <button id="clear-btn">Clear</button>
    <div id="chatbot-response"></div>

    <script>
        // Store the chat history
        let chatHistory = [
            {"user": "Hello! How are you?", "chatbot": "I'm doing great, thanks! What can I help you with today?"},
            {"user": "I'm feeling a bit stuck with my project. Can you help me?", "chatbot": "Of course! What specifically are you struggling with?"}
        ];

        // Add event listeners to the send button and input field
        document.getElementById("send-btn").addEventListener("click", () => {
            const userMessage = document.getElementById("user-input").value.trim();
            if (userMessage!== "") {
                // Add user message to chat history
                chatHistory.push({"user": userMessage, "chatbot": ""});
                document.getElementById("user-input").value = "";
                generateChatbotResponse(userMessage);
                updateChatWindow();
            }
        });

        document.getElementById("clear-btn").addEventListener("click", () => {
            chatHistory = [];
            document.getElementById("chat-window").innerHTML = "";
            document.getElementById("user-input").value = "";
        });

        // Generate chatbot response
        function generateChatbotResponse(userMessage) {
            const responses = {
                "hello": "Hi! How are you?",
                "stuck": "Of course! What specifically are you struggling with?"
            };
            const userMessageLower = userMessage.toLowerCase();
            for (const response in responses) {
                if (userMessageLower.includes(response)) {
                    chatHistory[chatHistory.length - 1].chatbot = responses[response];
                    return;
                }
            }
            chatHistory[chatHistory.length - 1].chatbot = "Sorry, I didn't understand that. Can you please rephrase?";
        }

        // Update chat window
        function updateChatWindow() {
            const chatWindow = document.getElementById("chat-window");
            chatWindow.innerHTML = "";
            chatHistory.forEach((message, index) => {
                const messageElement = document.createElement("div");
                messageElement.className = "message";
                messageElement.style.backgroundColor = index % 2 === 0? "#F2F2F2" : "#F5F5F5";
                const span = document.createElement("span");
                span.style.fontWeight = "bold";
                if (index % 2 === 0) {
                    span.textContent = "User";
                } else {
                    span.textContent = "Chatbot";
                }
                messageElement.appendChild(span);
                messageElement.appendChild(document.createTextNode(": "));
                messageElement.appendChild(document.createTextNode(message.user));
                messageElement.appendChild(document.createElement("br"));
                chatWindow.appendChild(messageElement);
            });
        }
    </script>
