// Function to count the occurrences of a word in a vector of words
int countWordOccurrences(const std::vector<std::string>& words, const std::string& targetWord) {
    int count = 0;
    for (const auto& word : words) {
        if (word == targetWord) {
            count++;
        }
    }
    return count;
}

