    public static boolean isPowerOfThree(int n) {
        if (n <= 0) {
            return false;
        }

        // 231 - 1 is just below the max value for an int: 3 * (int) Math.pow(10, 9).
        final int MAX = (int) (Math.pow(3, Math.ceil(Math.log(Integer.MAX_VALUE) / Math.log(3))) - 1);
        final int MIN = (int) (Math.pow(3, Math.floor(Math.log((long) 1) / Math.log(3))) - 1);

        return n > MIN && n < MAX;
    }

}
