std::string convertMarkdownToHtml(std::string &markdown) {
    std::string html;

    // Convert markdown headers
    size_t pos = 0;
    while ((pos = markdown.find("# ", pos)) != std::string::npos) {
        size_t hashPos = markdown.find(' ', pos + 2);
        if (hashPos != std::string::npos) {
            size_t hashCount = hashPos - pos - 1;
            html += "<h" + std::to_string(hashCount) + ">" + markdown.substr(pos + hashCount + 1, hashPos - (pos + hashCount) - 1) + "</h" + std::to_string(hashCount) + ">\n";
            pos = hashPos;
        }
    }

    // Convert markdown emphasis
    pos = 0;
    while ((pos = markdown.find("* ", pos)) != std::string::npos) {
        size_t endPos = markdown.find(" *", pos + 2);
        if (endPos != std::string::npos) {
            html += "<em>" + markdown.substr(pos + 2, endPos - (pos + 2)) + "</em>\n";
            pos = endPos;
        }
    }

    pos = 0;
    while ((pos = markdown.find("_ ", pos)) != std::string::npos) {
        size_t endPos = markdown.find(" _", pos + 2);
        if (endPos != std::string::npos) {
            html += "<em>" + markdown.substr(pos + 2, endPos - (pos + 2)) + "</em>\n";
            pos = endPos;
        }
    }

    // Convert markdown links
    pos = 0;
    while ((pos = markdown.find("[", pos)) != std::string::npos) {
        size_t endPos = markdown.find("]", pos + 1);
        if (endPos != std::string::npos) {
            size_t linkPos = markdown.find(" ", pos + 1);
            if (linkPos != std::string::npos) {
                size_t textPos = markdown.find(" ", linkPos + 1);
                if (textPos != std::string::npos) {
                    std::string anchor = markdown.substr(pos + 1, endPos - (pos + 1));
                    std::string text = markdown.substr(linkPos + 1, textPos - (linkPos + 1));
                    html += "<a href=\"#" + anchor + "\">" + text + "</a>\n";
                    pos = textPos;
                } else {
                    html += "<a href=\"#" + anchor + "\">" + markdown.substr(pos + 1, endPos - (pos + 1)) + "</a>\n";
                    pos = endPos;
                }
            }
        }
    }

    // Convert markdown bold and italic combinations
    pos = 0;
    while ((pos = markdown.find("**", pos)) != std::string::npos) {
        size_t endPos = markdown.find("**", pos + 2);
        if (endPos != std::string::npos) {
            html += "<strong>" + markdown.substr(pos + 2, endPos - (pos + 2)) + "</strong>\n";
            pos = endPos;
        }
    }

    pos = 0;
    while ((pos = markdown.find("~~", pos)) != std::string::npos) {
        size_t endPos = markdown.find("~~", pos + 2);
        if (endPos != std::string::npos) {
            html += "<del>" + markdown.substr(pos + 2, endPos - (pos + 2)) + "</del>\n";
            pos = endPos;
        }
    }

    return html;
}

int main() {
    std::string markdown = "# Header 1\n\n**Bold** and _italic_ text.\n\n[Link to Google](https://www.google.com)";
    std::string html = convertMarkdownToHtml(markdown);
    std::cout << html << std::endl;
    return 0;
}

