const TOKEN = process.env.BOT_TOKEN;

const client = new Discord.Client({ intents: ["GUILDS", "GUILD_MESSAGES"] });

// Define a command object with Discord.js SlashCommandBuilder
const banCommand = new SlashCommandBuilder()
    .setName('ban')
    .setDescription('Bans a user from the server')
    .addUserOption((option, builder) =>
        option.setRequired(true)
    );

// Event listener for when the bot connection is established
client.on('ready', async () => {
    console.log(`Logged in as ${client.user.tag}!`);
    // Register the ban command
    await client.application.commands.set(banCommand);
});

// Event listener for when a message is sent
