    // Only respond to the ban command in guilds
    if (message.content.startsWith('!ban') && message.guild) {
        // Extract the user specified in option (user argument of the slash command)
        const user = message.options.getMember('user').id;
        const banMention = message.mentions.members.first();

        // Check if the mentioned user is part of the guild
        if (banMention) {
            // Attempt to ban the user
