#include <stdlib.h>
#include <unistd.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <time.h>

#define WIDTH 80
#define HEIGHT 25

#define SNAKE_CHAR @#
#define EMPTY_CHAR ' '

// Snake struct
typedef struct {
    int *body;
    int length;
    int xOffset;
    int yOffset;
} Snake;

// Global variables
Snake snake;
FILE *imageFile;
time_t seed;

// Initialization function
void initGame(){
    imageFile = fopen("snakeGlyph.txt", "r");
    if(!imageFile){
        perror("Error opening file");
        exit(EXIT_FAILURE);
    }

    // Seed the random number generator
    seed = time(NULL);
    srand(seed);

    initializeSnake();
}

// Initialize the snake
void initializeSnake() {
    snake.body = (int*)malloc(3 * sizeof(int));
    snake.length = 3;
    snake.xOffset = HEIGHT / 2; // Start in the middle of the screen
    snake.yOffset = WIDTH / 2;

    snake.body[0] = snake.xOffset + (rand() % (HEIGHT - 6));
    snake.body[1] = snake.yOffset + (rand() % (HEIGHT - 6));
    snake.body[2] = snake.body[0] + 2;

    fseek(imageFile, rand() % 7, SEEK_SET);
}

// Move the snake
void moveSnake(int key) {
    int head = snake.body[snake.length - 1] + snake.xOffset;

    switch(key) {
        case ESCAPE:
            exit(0);
        case W:
            if(head > snake.xOffset + 2) {
                snake.body[0] = head - 2;
            }
            break;
        case S:
            if(head < HEIGHT - snake.xOffset) {
                snake.body[0] = head + 2;
            }
            break;
        case D:
            if(head > snake.yOffset) {
                snake.body[1] = head - 2;
            }
            break;
        case A:
            if(head < HEIGHT - snake.yOffset) {
                snake.body[1] = head + 2;
            }
            break;
        default:
            break;
    }

    for (int i = snake.length - 1; i > 0; i--) {
        snake.body[i] = snake.body[i - 1];
    }

    snake.length = snake.length + 1;
}

// Update the game screen
void updateScreen(){
    int y, x, i;

    // Print the snake
    for (i = 0; i < snake.length; i++) {
        x = snake.body[i] / HEIGHT;
        y = snake.body[i] % HEIGHT;

        // Seek the correct symbol if the snake part isn't in the last line
        if (y > 0) {
            fseek(imageFile, rand() % 7, SEEK_SET);
        }

        printf("%c", *SNAKE_CHAR);
        // Print the frame on screen
        printf("\r%c|", EMPTY_CHAR);
        printf("\r%c+", EMPTY_CHAR);
        printf("\r%c", EMPTY_CHAR);
        for (y = 0; y < HEIGHT - 1; y++) {
            printf("\r%c ", EMPTY_CHAR);
        }

        printf("\r%c+", SNAKE_CHAR);
        for (i = 0; i < HEIGHT - y - 1; i++) {
            printf("\r%c", EMPTY_CHAR);
        }
        printf("\r%c ", SNAKE_CHAR);
        for (y = 0; y < HEIGHT - 1; y++) {
            printf("\r%c ", EMPTY_CHAR);
        }

        printf("\r%c|", EMPTY_CHAR);
        printf("\r%c+", EMPTY_CHAR);
        printf("\r%c ", EMPTY_CHAR);
        for (y = 0; y < snake.length - 1; y++) {
            // Check if we are the snake part at position x, y
            if (SNAKE_CHAR == (char)fgetc(imageFile)) {
                printf("\r%c+", SNAKE_CHAR);
            } else {
                printf("\r%c *", EMPTY_CHAR);
            }
        }
        printf("\r%c+", EMPTY_CHAR);
        printf("\r%c|", EMPTY_CHAR);
        printf("\r%c+", EMPTY_CHAR);
        printf("\r%c", EMPTY_CHAR);

        fseek(imageFile, 0, SEEK_CUR);
    }
    fseek(imageFile, 0, SEEK_SET);

    // Print the level
    printf("\n");
    printf("-----------------------------------\n");
    printf("|                                 |\n");
    printf("|         Lvl x y size           |\n");
    printf("-----------------------------------\n");
    printf("|------------------------------|\n");
    printf("|     %d   %d   %d                 |\n", snake.length, snake.xOffset, snake.yOffset);
    printf("-----------------------------------\n");
    printf("\n");

    // Get a new glyph
    fseek(imageFile, rand() % 7, SEEK_SET);
}

// Main game loop
int main() {
    srand(seed);
    initGame();
    printf("SNAKE\n");

    while (1) {
        moveSnake(getchar());
        usleep(1000000); // Wait a bit before the next update
        updateScreen();
    }

    return 0;
}
