    if (!Array.isArray(numbersArray) || numbersArray.length !== 5) {
        console.log("Please provide exactly five numbers as input.");
        return;
    }

    for (let i = 0; i < numbersArray.length; i++) {
        if (isPrime(numbersArray[i])) {
            console.log(`Prime number found: ${numbersArray[i]}`);
        }
    }
}

// Example usage
