// Helper function to check if a number is prime
function isPrime(num) {
    // Zero and one are not prime numbers
    if (num <= 1) {
        return false;
    }
    if (num <= 3) {
        return true;
    }
    // Check from 2 to square root of num
    for (let i = 2; i <= Math.sqrt(num); i++) {
        if (num % i === 0) {
            return false;
        }
    }
    return true;
}

// Function to check for prime numbers in the given array
