    // Early check for array length
    if (arr.length !== 5) {
        console.log("Please provide an array of exactly 5 numbers.");
        return;
    }

    for(let i = 0; i < arr.length; i++) {
        if (isPrime(arr[i])) {
            console.log(`Prime number found: ${arr[i]}`);
        }
    }
}

// Example usage:
findPrimeNumbers([1, 2, 3, 4, 5]); // This should print out "Prime number found: 2" and "Prime number found: 3"
```

