        size_t headerEnd = markdownText.find('#', headerLevel * 4);
        if (headerEnd != std::string::npos) {
            std::string headerText = markdownText.substr(headerLevel * 4, headerEnd - headerLevel * 4);
            htmlText += "&lt;h" + std::to_string(headerLevel) + "&gt;";
            for (char c : headerText) {
