/**
 * This function takes an array of 5 numbers as arguments and checks if 
 * any of the numbers are prime numbers.
 * If it finds a prime number, it prints that number to the console.
 * 
 * @param {number[]} numbers - An array of 5 numbers
 */
function checkPrimes(numbers) {
  // Validate input array
  if (numbers.length !== 5) {
    console.log("Please provide an array of exactly 5 numbers.");
    return;
  }

  function isPrime(number) {
    // 0, 1, and negative numbers are not prime
    if (number <= 1) return false;
    // 2 is a prime number
    if (number === 2) return true;
    // Check divisibility by numbers other than 1 and self
    for (let i = 2; i <= Math.sqrt(number); i++) {
      if (number % i === 0) {
        return false;
      }
    }
    return true;
  }

  for (let i = 0; i < numbers.length; i++) {
    if (isPrime(numbers[i])) {
      console.log(`Found a prime number: ${numbers[i]}`);
      return; // Stop checking after finding the first prime number
    }
  }

  console.log("No prime numbers found in the list.");
}

// Example usage:
checkPrimes([2, 4, 6, 8, 10]); // Output: Found a prime number: 2
checkPrimes([1, 3, 4, 7, 9]); // Output: Found a prime number: 3
```

