```javascript
const { Client } = require('dpscord.js');

const client = new Client({
  token: 'YOUR_BOT_TOKEN',
  activities: ['Listening for commands'],
});

client.on('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
});

// Your command handling routes
client.on('message', (message) => {
  if (message.content.startsWith('/your_prefix')) {
    if (message.content.startsWith('/ban')) {
      // Ban command logic
    }
